import React from 'react'
import {Amount,AmountText,AmountWrapper,ViewRedirect} from './style';

export default function BalanceComponent(props) {
    return (
        <AmountWrapper>
            <AmountText>Current Balance</AmountText>
            <i style={{fontStyle:"oblique"}}className="fa fa-info-circle"></i>
            <Amount><span>&#36;</span>120,000</Amount>
            {props.redirect?<ViewRedirect href="#">VIEW TRANSACTION SUMMARY<span>&#8594;</span></ViewRedirect>:null}
        </AmountWrapper>
    )
}
